import * as actionTypes from "./actionTypes";

export const authorStart= () => {
    return {
        type:actionTypes.AUTHOR_START
    };
};

export const authorSuccess = (authorData) => {
    return {
        type:actionTypes.AUTHOR_SUCCESS,
        authorData:authorData
    };
};

export const authorFail= (error) => {
    return {
        type:actionTypes.AUTHOR_FAIL,
        error:error
    };
};

export const author= (email,password) => {
    return dispatch => {
        dispatch(authorStart());
    };
};
