import * as actionTypes from "./actionTypes";
import axios from "axios";

export const authorStart= () => {
    return {
        type:actionTypes.AUTHOR_START
    };
};

export const authorSuccess = (authorData) => {
    return {
        type:actionTypes.AUTHOR_SUCCESS,
        authorData:authorData
    };
};

export const authorFail= (error) => {
    return {
        type:actionTypes.AUTHOR_FAIL,
        error:error
    };
};

export const author= (email,password) => {
    return dispatch => {
        dispatch(authorStart());
        const authorData= {
            email:email,
            password:password,
            returnSecureToken: true
        }
        axios.post("https://www.googleapis.com/identitytoolkit/v3/relyingparty/verifyCustomToken?key=AIzaSyBkx9TerY5zZL4QfVuERTS5g0hjq2nsq1Q", authorData)
        .then(response => {
            console.log(response);
            dispatch(authorSuccess(response.data));
        })
        .catch(err => {
            console.log(err);
            dispatch(authorFail(err));
        });
    };
};
